mapper.select:
SELECT c.uid AS uid, c.room_id AS roomId, c.read_time AS readTime, r.active_time AS activeTime, r.last_msg_id AS lastMsgId FROM contact AS c JOIN room AS r ON c.room_id = r.id WHERE uid = #{uid}
AND r.active_time < DATE_FORMAT(#{request.cursor}, '%Y-%m-%d %H:%i:%s')
ORDER BY r.active_time DESC LIMIT 0, #{request.pageSize}
